// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./TokenImplementation.sol";

/**
 * @title TokenImplementationV2
 * @dev Improved version of the token implementation with additional features
 */
contract TokenImplementationV2 is TokenImplementation {
    // New state variables for V2
    bool public paused;
    mapping(address => bool) public blacklisted;
    uint256 public maxTransferAmount;

    /**
     * @dev Initializes the V2 specific variables
     * @param _maxTransferAmount The maximum amount that can be transferred in a single transaction
     */
    function initializeV2(uint256 _maxTransferAmount) public reinitializer(2) {
        maxTransferAmount = _maxTransferAmount;
        paused = false;
    }

    /**
     * @dev Pauses all token transfers
     */
    function pause() public onlyOwner {
        paused = true;
    }

    /**
     * @dev Unpauses all token transfers
     */
    function unpause() public onlyOwner {
        paused = false;
    }

    /**
     * @dev Adds an address to the blacklist
     * @param account The address to blacklist
     */
    function blacklist(address account) public onlyOwner {
        blacklisted[account] = true;
    }

    /**
     * @dev Removes an address from the blacklist
     * @param account The address to remove from the blacklist
     */
    function unblacklist(address account) public onlyOwner {
        blacklisted[account] = false;
    }

    /**
     * @dev Sets the maximum transfer amount
     * @param amount The new maximum transfer amount
     */
    function setMaxTransferAmount(uint256 amount) public onlyOwner {
        maxTransferAmount = amount;
    }

    /**
     * @dev Hook that is called before any transfer of tokens.
     * We're using _update in the newer OZ version
     */
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        require(!paused, "TokenImplementationV2: token transfer while paused");
        require(!blacklisted[from], "TokenImplementationV2: sender is blacklisted");
        require(!blacklisted[to], "TokenImplementationV2: recipient is blacklisted");

        if (maxTransferAmount > 0) {
            require(amount <= maxTransferAmount, "TokenImplementationV2: transfer amount exceeds maximum");
        }

        super._update(from, to, amount);
    }

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     */
    uint256[47] private __gap;
}

contracts/TokenUpgrader.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/interfaces/IERC1967.sol";
import "./TokenImplementation.sol";

/**
 * @title TokenUpgrader
 * @dev Contract for upgrading individual token instances
 */
contract TokenUpgrader is Ownable {
    // Events
    event TokenUpgraded(address indexed tokenAddress, address indexed newImplementation);

    /**
     * @dev Constructor that sets the owner of the upgrader
     */
    constructor() Ownable(msg.sender) {}

    /**
     * @dev Upgrades a specific token to a new implementation
     * @param tokenAddress The address of the token to upgrade
     * @param newImplementation The address of the new implementation
     */
    function upgradeToken(address tokenAddress, address newImplementation) external onlyOwner {
        require(tokenAddress != address(0), "TokenUpgrader: token address cannot be zero");
        require(newImplementation != address(0), "TokenUpgrader: new implementation cannot be zero");

        // Ensure the token owner is this contract before upgrading
        try TokenImplementation(tokenAddress).owner() returns (address tokenOwner) {
            require(tokenOwner == address(this), "TokenUpgrader: not the owner of the token");
        } catch {
            revert("TokenUpgrader: failed to check token ownership");
        }

        // Get the UUPSUpgradeable interface and call upgradeTo
        // We use a low-level call to handle the function call
        (bool success, ) = tokenAddress.call(
            abi.encodeWithSignature("upgradeTo(address)", newImplementation)
        );
        require(success, "TokenUpgrader: upgrade failed");

        emit TokenUpgraded(tokenAddress, newImplementation);
    }

    /**
     * @dev Transfers ownership of a token to a new owner
     * @param tokenAddress The address of the token
     * @param newOwner The address of the new owner
     */
    function transferTokenOwnership(address tokenAddress, address newOwner) external onlyOwner {
        require(tokenAddress != address(0), "TokenUpgrader: token address cannot be zero");
        require(newOwner != address(0), "TokenUpgrader: new owner cannot be zero");

        TokenImplementation token = TokenImplementation(tokenAddress);

        // Ensure the token owner is this contract before transferring ownership
        try token.owner() returns (address tokenOwner) {
            require(tokenOwner == address(this), "TokenUpgrader: not the owner of the token");
        } catch {
            revert("TokenUpgrader: failed to check token ownership");
        }

        // Transfer ownership
        token.transferOwnership(newOwner);
    }
}

